import React from 'react';
import { connect } from 'react-redux';
import {
    Link,
    withRouter
} from 'react-router-dom';
import { addToCart } from '../../actions/CartActions';

const mapStateToProps = (state, ownProps) => {
    return {
        items: state.items,
        id: ownProps.id
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        addToCart: (id) => { dispatch(addToCart(id)) }
    };
}

class Item extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.location.state.id,
            img: this.props.location.state.img,
            title: this.props.location.state.title,
            desc: this.props.location.state.desc,
            price: this.props.location.state.price
        }

    }

    handleAddToCart = (id) => {
        this.props.addToCart(id);
    }

    render() {
        return (
            <div className="item-details" key={this.props.id}>
                <div className="item-details-img">
                    <img src={this.props.img} alt={this.props.title} />
                </div>
                <div className="item-details-info">
                    <p className="item-details-title">{this.props.title}</p>
                    <p className="item-details-price"><b>${this.props.price}</b></p>
                    <p className="item-details-desc">{this.props.desc}</p>
                    <div className="item-details-add-to-cart">
                        <Link to="/cart" onClick={ () => { this.handleAddToCart(this.props.id) } }>
                            <button>Add to Bag</button>
                        </Link>
                    </div>
                </div>
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Item));
