import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { 
    addToCart, 
    changeSize
} from '../../actions/CartActions';

const mapStateToProps = (state) => {
    return {
        item: state.items
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        addToCart: (id) => { dispatch(addToCart(id)) },
        changeSize: (id) => { dispatch(changeSize(id)) }
    }
}

class Item extends React.Component {
    handleClick = (id) => {
        this.props.addToCart(id)
    }

    handleSizeChange = (id) => {
        this.props.changeSize(id)
    }

    render() {
        const {itemID} = props.location.state;
        return (
            <div className="item" key={itemID.id}>
                    <div className="item-image">
                        <img src={itemID.img} alt={itemID.title} />
                    </div>
                    <div className="card-component">
                        <span className="item-title">{itemID.title}</span>
                        <Link to="/" className="add-item" onClick={ ()=>{this.handleClick(itemID.id)} }><i class="fa fa-plus-circle"></i></Link>
                        <p className="item-price"><b>${itemID.price}</b></p>
                        <p className="item-desc">{itemID.desc}</p>
                    </div>
                </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Item);
