import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup';

const schema = yup.object().shape({
    name: yup.string().required('Name required'),
    email: yup.string().email().required('Valid email required'),
    message: yup.string().min(10).max(600).required('Message required')
});

class ContactForm extends React.Component {
    render() {
        return (
            <div className="contact-form-content">
                <Formik
                    validationSchema={schema}
                    initialValies={{
                        name: '', 
                        email: '', 
                        message: '' 
                    }}
                    onSubmit={values => {
                        fetch("/", {
                          method: "POST",
                          headers: { "Content-Type": "application/x-www-form-urlencoded" },
                          body: serialize({
                            "form-name": "contact",
                            ...values,
                          }),
                        })
                          .then(() => navigateTo("#success"))
                          .catch(error => navigateTo("#error"))
                      }}
                    >
                        {({ isSubmitting }) => (
                            <Form action="/contact" id="contact-form" method="POST" role="form">
                                <label for name="name">Name</label>
                                <Field type="name" name="name" id="name" />
                                <ErrorMessage name="name" component="span" />
                                <label for name="email">Email</label>
                                <Field type="email" name="email" id="email" />
                                <ErrorMessage name="email" component="span" />
                                <label for name="message">Message</label>
                                <Field component="textarea" name="message" id="message" />
                                <ErrorMessage name="message" component="span" />
                                <button type="submit" disabled={isSubmitting} >
                                    Submit
                                </button>
                                <div>
                                    { window.location.hash === '#success' &&
                                <div id="contact-success"><p>Your message has been sent!</p>
                                <span>Please allow up to 48 hours for a response</span>
                                </div> }
                                    { window.location.hash === '#error' && <div id="contact-error"><p>An error occured while submitting the form.</p></div> }
                                </div>
                            </Form>
                        )}
                </Formik>
            </div>
        );
    }
}

export default ContactForm;
