import React from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { 
    addToCart, 
    changeSize
} from '../../actions/CartActions';

class Item extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            item: null
        }
    }

    componentDidMount() {
        this.setState({
            item: this.props.location.state
        })
    }

    render() {
        let itemView = this.props.location.state.map(item =>{
            return (
                <div className="item-card" key={item.id}>
                    <div className="item-image">
                        <Link to = 
                            {{ pathname: `/products/${item.category}`,
                            search: `?id=${item.id}`,
                            state: {id: `${item.id}`} }}
                            component={ Item }>
                                <img src={item.img} alt={item.title} />
                        </Link>
                    </div>
                    <div className="card-component">
                        <span className="item-title">{item.title}</span>
                        <Link to="/" className="add-item" onClick={ ()=>{this.handleClick(item.id)} }><i class="fa fa-plus-circle"></i></Link>
                        <p className="item-price"><b>${item.price}</b></p>
                        <p className="item-desc">{item.desc}</p>
                    </div>
                </div>
            )
        })

        return (
            <div className="new-arrivals-section">
                <div className="new-arrivals-items">
                    {itemView}
                </div>
            </div>
        );
    }
}

export default withRouter(Item);
