import React from 'react';
import { connect } from 'react-redux';
import {
    Link,
    withRouter
} from 'react-router-dom';
import { addToCart } from '../../actions/CartActions';

const mapStateToProps = (state) => {
    return {
        items: state.items
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        addToCart: (id) => { dispatch(addToCart(id)) }
    };
}

class Item extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            item: this.props.location.state
        }
    }

    handleAddToCart = (id) => {
        this.props.addToCart(id);
    }

    render() {
        let itemView = this.props.items.map(item =>{
            let detail = this.state.item;
            return (
                <div className="item-details" key={detail.id}>
                        <div className="item-details-img">
                            <img src={detail.img} alt={detail.title} />
                        </div>
                        <div className="item-details-info">
                            <p className="item-details-title">{detail.title}</p>
                            <p className="item-details-price"><b>${detail.price}</b></p>
                            <p className="item-details-desc">{detail.desc}</p>
                            <div className="item-details-add-to-cart">
                                <Link to="/cart" onClick={()=>{this.handleAddToCart(detail.id)}}>
                                    <button>Add to Bag</button>
                                </Link>
                            </div>
                        </div>
                    </div>
            )
        })

        return (
            <div>
                {itemView}
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Item));
