import React from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { 
    addToCart, 
    // changeSize
} from '../../actions/CartActions';

const mapStateToProps = (state) => {
    return {
        items: state.items
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        addToCart: (id) => { dispatch(addToCart(id)) }
    }
}

class Item extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            item: this.props.location.state
        }
    }

    render() {
        handleClick = (id) => {
            this.props.addToCart(id);
        }

        let item = this.state.item;
        return (
            <div className="item-details">
                <div className="item-details-img">
                    <img src={item.img} alt={item.title} />
                </div>
                <div className="item-details-info">
                    <p className="item-details-title">{item.title}</p>
                    <p className="item-details-price"><b>${item.price}</b></p>
                    <p className="item-details-desc">{item.desc}</p>
                    <Link to="/" className="add-item" onClick={ ()=>{this.handleClick(item.id)} }><i class="fa fa-plus-circle"></i></Link>
                </div>
            </div>
        );
    }
}

export default withRouter(Item);
