import React from 'react';
import { connect } from 'react-redux';
import {
    Link,
    withRouter
} from 'react-router-dom';
import { addToCart } from '../../actions/CartActions';

const mapStateToProps = (state) => {
    return {
        items: state.items,
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        addToCart: (id) => { dispatch(addToCart(id)) }
    };
}

class Item extends React.Component {
    handleAddToCart = (id) => {
        this.props.addToCart(id);
    }

    render() {
        const items = this.state.items;
        return (
            <div className="item-details" key={items.id}>
                <div className="item-details-img">
                    <img src={items.img} alt={items.title} />
                </div>
                <div className="item-details-info">
                    <p className="item-details-title">{items.title}</p>
                    <p className="item-details-price"><b>${items.price}</b></p>
                    <p className="item-details-desc">{items.desc}</p>
                    <div className="item-details-add-to-cart">
                        <Link to="/cart" onClick={ () => { this.handleAddToCart(items.id) } }>
                            <button>Add item #{items.id} to Bag</button>
                        </Link>
                    </div>
                </div>
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Item));
