import React from 'react';
import { connect } from 'react-redux';
import { addGiftCard } from '../../actions/CartActions';

const mapStateToProps = (state) => {
    return {
        value: state.value
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        addValue: (value) => { dispatch(addGiftCard(value)) }
    }
}

class GiftCards extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            giftCardVal: 0
        }
    }

    handleSetVal = value => {
        this.setState({
            giftCardVal: value
        })
    }

    render() {
        return(
            <div>
                <h1>Gift Cards</h1>
                <h2>Select a gift card amount below to add to your cart.</h2>
                <div className="giftcard-section">
                    <div className="giftcard-options">
                        <button onClick={() => {this.handleSetVal(25)}}>$25.00</button>
                        <button onClick={() => {this.handleSetVal(50)}}>$50.00</button>
                        <button onClick={() => {this.handleSetVal(75)}}>$75.00</button>
                        <button onClick={() => {this.handleSetVal(100)}}>$100.00</button>
                    </div>
                    <div className="giftcard-purchase">
                        <button href="/">Purchase</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(GiftCards);
