import React from 'react';
import { connect } from 'react-redux';
import {
    ADD_SHIPPING,
    SUBTRACT_SHIPPING
} from '../constants/ActionTypes';

const mapStateToProps = (state) => {
    return {
        addedItems: state.addedItems,
        total: state.total
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        addShipping: () => {
            dispatch({type: ADD_SHIPPING})
        },
        subtractShipping: () => {
            dispatch({type: SUBTRACT_SHIPPING})
        }
    }
}

class CalculateTotal extends React.Component {    
    handleChecked = (e) => {
        if (e.target.checked) {
            this.props.addShipping();
        } else {
            this.props.subtractShipping();
        }
    }

    render() {
        // Set shipping cost as a variable
        let shippingCost = 10;

        // Logic to add and subtract shipping costs
        if (action.type === ADD_SHIPPING) {
            return{
                ...state,
                total: state.total + shippingCost
            }
        }

        if (action.type === SUBTRACT_SHIPPING) {
            return {
                ...state,
                total: state.total - shippingCost
            }
        }

        return (
            <div className="container">
                <div className="shipping-section">
                    <li className="shipping-item">
                        <label>
                            <input type="checkbox" ref="shipping" onChange={this.handleChecked} />
                            <span>Shipping (${shippingCost})</span>
                        </label>
                    </li>
                    <li className="shipping-item">
                        <p>Total: ${this.props.total}</p>
                    </li>
                </div>

                <div className="checkout">
                    <button className="checkout-btn">Checkout</button>
                </div>
            </div>
        )
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CalculateTotal);
