import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { 
    addQuantity,
    minusQuantity, 
    removeItem 
} from '../actions/CartActions';

const mapStateToProps = (state) => {
    return {
        items: state.addedItems
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        addQuantity: (id) => {dispatch(addQuantity(id))},
        minusQuantity: (id) => {dispatch(minusQuantity(id))},
        removeItem: (id) => {dispatch(removeItem(id))}
    }
}

class Items extends React.Component {
    // Increase item quantity
    handleAddQuantity = (id) => {
        this.props.addQuantity(id);
    }

    // Decrease item quantity
    handleMinusQuantity = (id) => {
        this.props.minusQuantity(id);
    }

    // Remove item
    handleRemoveItem = (id) => {
        this.props.removeItem(id);
    }

    render() {
        let addedItems = this.props.items.length;
        if(!addedItems) {
            return null;
        } else {
            this.props.items.map(item => {
                return (
                    <li className="cart-item-card">
                        <div className="cart-item-image">
                            <img src={item.img} alt={item.title} />
                        </div>

                        <div className="cart-item-info">
                            <span className="cart-title">{item.title}</span>
                            <p className="cart-item-desc">{item.desc}</p>
                            <p className="cart-item-price">${item.price}</p>
                            <p className="cart-item-qty">Qty: {item.quantity}</p>

                            <Link to="/cart">
                                <span className="add-minus-btn"><i class="fa fa-plus-circle"
                                onClick={ () => {this.handleAddQuantity(item.id)} }
                                ></i></span>
                            </Link>
                            <Link to="/cart">
                            <span className="add-minus-btn"><i class="fa fa-minus-circle" 
                                onClick={ () => {this.handleMinusQuantity(item.id)} }
                                ></i></span>
                            </Link>
                            <span className="add-minus-btn"><i class="fa fa-times-circle" 
                            onClick={ () => {this.handleRemoveItem(item.id)} }></i></span>
                        </div>
                    </li>
                )
            })
        }

        return (
            <div className="container">
                {addedItems}
            </div>
        )
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Items);
