import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { 
    addToCart, 
    changeSize
} from '../../actions/CartActions';

const mapStateToProps = (state) => {
    return {
        item: state.items
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        addToCart: (id) => { dispatch(addToCart(id)) },
        changeSize: (id) => { dispatch(changeSize(id)) }
    }
}

class Item extends React.Component {
    constructor() {
        super();

        this.state = {
            item: null
        }
    }

    componentDidMount() {
        const { itemID } = this.props.location.state;
        fetch(`/products/:category/?id=${itemID}`)
        .then((item) => {
            this.setState(() => ({ item }))
        })
    }

    handleClick = (id) => {
        this.props.addToCart(id)
    }

    handleSizeChange = (id) => {
        this.props.changeSize(id)
    }

    render() {
        let item;
        return (
            <div className="item">
                <div className="item-view" key={item.id}>
                    <div className="item-image">
                        <img src={item.img} alt={item.title} />
                    </div>
                    <div className="card-component">
                        <span className="item-title">{item.title}</span>
                        <Link to="/" className="add-item" onClick={ ()=>{this.handleClick(item.id)} }><i class="fa fa-plus-circle"></i></Link>
                        <p className="item-price"><b>${item.price}</b></p>
                        <p className="item-desc">{item.desc}</p>
                    </div>
                </div>
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Item);
